using System;
using System.Collections.Generic;
using System.IO;

namespace FileExtensionScan
{
    class Program
    {
        Dictionary<String, int> pList = new Dictionary<String, int>(); //{}
        int fileNum = 0;

        public void GetFileNum(string srcPath)
        {
            try
            {
                // 
                string[] fileList = System.IO.Directory.GetFileSystemEntries(srcPath);
                // 
                foreach (string file in fileList)
                {
                    // GetFileNum(string srcPath)
                    if (Directory.Exists(file))
                        GetFileNum(file);
                    else
                    {
                        fileNum++;
                        string ext = Path.GetExtension(file).ToLower();
                        if (pList.ContainsKey(ext) == false)
                            pList.Add(ext, 1);
                        else
                            pList[ext]++;
                    }
                }

            }
            catch (Exception e)
            {
                Console.WriteLine(e.ToString());
            }
        }

        static void Main(string[] args)
        {
            Program p = new Program();
            p.GetFileNum(args[0]); //
            Console.WriteLine("" + p.fileNum); //

            foreach (var dic in p.pList)
            {
                Console.WriteLine("{0}  {1}", dic.Key, dic.Value);
            }
        }
    }
}

```

